import React from 'react';
import './App.css';

class App extends Component {
  state = {
    count: 0,

  }

  handleCountInc = _ => {
    let { count } = this.state

    this.setState({ count: count + 1 })

  }
  handleCountDec = _ => {
    let { count } = this.state

    this.setState({ count: count - 1 })

  }

  render () {
    const { count } = this.state
    return (
      <div className="App">
       <h1>Hello World!</h1>
       <h3>Count: {count}</h3>
       <button onClick={this.handleCountInc} >Count Up!</button>
       <button onClick={this.handleCountDec} >Count Down!</button>

       {/* <Form />
       <List /> */}
      </div>
    )
  }
}

export default App
